import { mapGetters, mapMutations } from 'vuex'
export default {
  data () {
    return {
      state: false,//Âà§Êñ≠ÊòØ‰∏çÊòØÊú¨‰∫∫
      user: {},//Áî®Êà∑Âü∫Êú¨‰ø°ÊÅØ
      detail: {},//Áî®Êà∑ËØ¶ÊÉÖ
      loading: true,
      width: '',
      height: 230,
      //ÂàùÂßãÂåñËÉåÊôØÂõæÊèí‰ª∂
      croppa: {},
      //ÂàùÂßãÂåñÂõæÁâáÊªëÂä®ÊîæÂ§ßÁº©Â∞èinput range
      sliderVal: 0,
      sliderMin: 0,
      sliderMax: 0,
      root: '',//ÁΩëÁ´ôapiÊ†πÁõÆÂΩï
      alterImgFlag: true,//ÊòØÂê¶Âá∫Áé∞Â§ÑÁêÜËÉåÊôØÂõæÁâáÊåâÈíÆ
      freshFlag: false,//Âà§Êñ≠ÊòØÂê¶‰∏ä‰º†ËøáÂõæÁâá
      alterDetail: true,//Ë∑≥ËΩ¨Âà∞ÁºñËæë‰∏™‰∫∫ËµÑÊñôÂºÄÂÖ≥
      cursorFlag: false,//ÊéßÂà∂Èº†Ê†áÂú®ËÉåÊôØÂõæ‰∏äÁöÑÊ†∑Âºè
      centerDialogVisible: false,//‰øÆÊîπÂ§¥ÂÉèÁöÑÂØπËØùÊ°ÜÊéßÂà∂
    }
  },
  computed: {
    ...mapGetters([
      'verbState',//ÁôªÂΩïÁä∂ÊÄÅ
      'token',//ÁôªÂΩïÁöÑÁî®Êà∑ÁöÑtoken
      'self',//Ëé∑ÂèñÂΩìÂâçÁôªÂΩïÁöÑÁî®Êà∑‰ø°ÊÅØ
    ]),
  },
  //ÁõëÂê¨Ë∑ØÁî±ÂèòÂåñ
  watch: {
    $route () {
      //Ëé∑ÂèñÁΩëÈ°µurlÂà§Êñ≠ÊòØÂê¶Âú®Êõ¥ÊîπËØ¶ÊÉÖÈ°µÈù¢
      if (this.$route.path.endsWith('alterDetail')) this.alterDetail = false;
    }
  },
  created: function () {

    //Ëé∑ÂèñÊµèËßàÂô®Á™óÂè£ÁöÑÂÆΩÂ∫¶ ÁÑ∂ÂêéËµãÂÄºÁªôËÉåÊôØÂõæÁâáÂÆΩÂ∫¶
    this.width = window.screen.width;
    //Âà§Êñ≠ÊòØÂê¶ÁôªÂΩï
    this.state = localStorage.getItem('verbState');
    //Ëé∑ÂèñË∑ØÁî±‰º†ÈÄíËøáÊù•ÁöÑÁî®Êà∑id
    this.id = this.$route.params.id;

    //Êï∞ÊçÆ
    let paramsObj = {
      id: this.id,
    }
    //ÂèëÈÄÅaxios Ëé∑Âèñ‰º†ÈÄíËøáÊù•idÂØπÁî®ÁöÑÁî®Êà∑ÁöÑÊï∞ÊçÆ
    this.$server.getInformation(paramsObj).then(data => {
      //Ëé∑ÂèñÂà∞Áî®Êà∑ÁöÑÂü∫Êú¨‰ø°ÊÅØ
      this.user = data;
      //Ëé∑ÂèñÂà∞Áî®Êà∑ÁöÑËØ¶ÁªÜ‰ø°ÊÅØ
      this.detail = data.user_detail;
      //Âà§Êñ≠Áî®Êà∑ÊòØÂê¶ÊúâËÉåÊôØÂõæÁâá ‰∏îÊòØÂê¶ÊòØÊú¨‰∫∫È°µÈù¢ ÁÑ∂ÂêéÊõ¥ÊîπÈ´òÂ∫¶
      //‰πãÊâÄ‰ª•‰ΩøÁî®ÂÆöÊó∂Âô® ÊòØÂõ†‰∏∫ÊàëÂà´Êó†‰ªñÊ≥ï Âè™ËÉΩÁî®ÂÆöÊó∂Âô®Âª∂ËøüÁÑ∂ÂêéËé∑ÂèñÊï∞ÊçÆ
      setTimeout(() => {
        if (this.user.backgroundImg != null || this.user.id == this.self.id) {
          this.state = true;
          this.height = 430;
        }
      }, 200);

    }).catch(err => {
      // eslint-disable-next-line no-console
      console.log(err);
    })
  },
  mounted: function () {
    //ÁΩëÈ°µÂä†ËΩΩÁä∂ÊÄÅ
    this.loading = false;
    //apiÂú∞ÂùÄ
    this.root = this.$URL;
    //Ëé∑ÂèñÁΩëÈ°µurlÂà§Êñ≠ÊòØÂê¶Âú®Êõ¥ÊîπËØ¶ÊÉÖÈ°µÈù¢
    if (this.$route.path.endsWith('alterDetail')) this.alterDetail = false;
  },
  methods: {
    ...mapMutations({
      setuser: 'SET_USER',
    }),
    onFileTypeMismatch () {
      this.$message({
        message: 'Êñá‰ª∂Ê†ºÂºè‰∏çÂØπÂë¶ üò≥',
        type: 'warning',
      })
    },
    onNewImage () {
      this.sliderVal = this.croppa.scaleRatio
      this.sliderMin = this.croppa.scaleRatio / 2
      this.sliderMax = this.croppa.scaleRatio * 5
    },

    onSliderChange (evt) {
      var increment = evt.target.value
      this.croppa.scaleRatio = +increment
    },

    onZoom () {
      // To prevent zooming out of range when using scrolling to zoom
      // if (this.sliderMax && this.croppa.scaleRatio >= this.sliderMax) {
      //   this.croppa.scaleRatio = this.sliderMax
      // } else if (this.sliderMin && this.croppa.scaleRatio <= this.sliderMin) {
      //   this.croppa.scaleRatio = this.sliderMin
      // }

      this.sliderVal = this.croppa.scaleRatio
    },
    upload () {

      //Âà§Êñ≠ÊòØÂê¶ÊúâÂõæÁâáÈÄâ‰∏≠
      if (!this.croppa.hasImage()) {
        this.$message({
          message: '‰Ω†ËøòÊ≤°ÊúâÈÄâÊã©ÂõæÁâá üò≥',
          type: 'warning'
        });

        return false;
      }

      //Ëé∑ÂèñÂà∞Êñá‰ª∂ ‰ΩÜÊòØÊòØblob ‰∫åËøõÂà∂ËµÑÊ∫ê
      this.croppa.generateBlob((blob) => {
        //‰∫åËøõÂà∂ËΩ¨Êñá‰ª∂
        const file = new File([blob], 'backImg', {
          type: 'image/jpeg',
        });
        var fd = new FormData()
        fd.append('image', file);
        fd.append('type', 'backgroundImg');

        //ÂèëÈÄÅaxiosÊõ¥ÊîπÂõæÁâá
        this.$server.uploadImg(fd).then(data => {
          //Êõ¥ÊîπÈªòËÆ§ÂõæÁâáË∑ØÂæÑ
          this.detail.backgroundImg = data;
          //Êõ¥ÊîπÊåâÈíÆÁä∂ÊÄÅ
          this.alterImgFlag = !this.alterImgFlag;
          //‰∏ä‰º†ÊàêÂäüÊèêÁ§∫
          this.$message({
            type: 'success',
            message: '‰∏ä‰º†ÊàêÂäü üòä'
          });
        }).catch(err => {
          // eslint-disable-next-line no-console
          console.log('err', err);
        })


        return false;
      })
    },
    alterImg () {
      this.alterImgFlag = !this.alterImgFlag;
      this.cursorFlag = !this.cursorFlag;
      this.croppa.refresh()
    },
    removeBackgroundImg () {
      this.$confirm('ÁßªÈô§ËÉåÊôØ, ÊòØÂê¶ÁªßÁª≠?', 'ÊèêÁ§∫', {
        confirmButtonText: 'Á°ÆÂÆö',
        cancelButtonText: 'ÂèñÊ∂à',
        type: 'warning'
      }).then(() => {

        //ÂèÇÊï∞
        let paramsObj = {
          type: 'backgroundImg',
        }
        //ÂèëÈÄÅaxios Âà†Èô§ÂõæÁâá
        this.$server.deleteImg(paramsObj).then(data => {
          if (data == 2) {
            this.$message({
              type: 'warning',
              message: 'ËøòÊ≤°ÊúâËÉåÊôØÂõæ üòì'
            });
            return false;
          }
          //Êõ¥ÊîπÈªòËÆ§ÂõæÁâáË∑ØÂæÑ
          this.detail.backgroundImg = '';
          //Ëß¶ÂèëÊèí‰ª∂ÁßªÈô§
          this.croppa.remove();
          //ÊàêÂäüÊèêÁ§∫
          this.$message({
            type: 'success',
            message: 'ÁßªÈô§ÊàêÂäü!'
          });
          //Êõ¥ÊîπÊåâÈíÆÁä∂ÊÄÅ
          this.alterImgFlag = !this.alterImgFlag;

        }).catch(err => {
          // eslint-disable-next-line no-console
          console.log(err)
        })

      }).catch(() => {
        this.$message({
          type: 'info',
          message: 'Â∑≤ÂèñÊ∂à'
        });
      });
    },
    alterDetails () {
      this.alterDetail = !this.alterDetail;
    },
    //Êõ¥ÊîπÂ§¥ÂÉèÁöÑcroppa Êõ¥Êîπ‰∏∫ÂúÜÂúà
    onInit () {
      this.croppa.addClipPlugin(function (ctx, x, y, w, h) {
        /*
         * ctx: canvas context
         * x: start point (top-left corner) x coordination
         * y: start point (top-left corner) y coordination
         * w: croppa width
         * h: croppa height
        */

        ctx.beginPath()
        ctx.arc(x + w / 2, y + h / 2, w / 2, 0, 2 * Math.PI, true)
        ctx.closePath()
      })
    },
    //‰∏ä‰º†Â§¥ÂÉèÁöÑÊñπÊ≥ï
    uploadAvatar () {

      //Âà§Êñ≠ÊòØÂê¶ÊúâÂõæÁâáÈÄâ‰∏≠
      if (!this.croppa.hasImage()) {
        this.$message({
          message: '‰Ω†ËøòÊ≤°ÊúâÈÄâÊã©ÂõæÁâá üò≥',
          type: 'warning'
        });

        return false;
      }

      //Ëé∑ÂèñÂà∞Êñá‰ª∂ ‰ΩÜÊòØÊòØblob ‰∫åËøõÂà∂ËµÑÊ∫ê
      this.croppa.generateBlob((blob) => {
        //‰∫åËøõÂà∂ËΩ¨Êñá‰ª∂
        const file = new File([blob], 'backImg', {
          type: 'image/jpeg',
        });
        var fd = new FormData()
        fd.append('image', file);
        fd.append('type', 'avatar');

        //ÂèëÈÄÅaxiosÊõ¥ÊîπÂõæÁâá
        this.$server.uploadImg(fd).then(data => {

          if (data == 2) {
            //‰∏ä‰º†Â§±Ë¥•ÊèêÁ§∫
            this.$message({
              type: 'success',
              message: '‰∏ä‰º†Â§±Ë¥• üòì'
            });
            return false;
          }
          //‰∏ä‰º†ÊàêÂäüÊèêÁ§∫
          this.$message({
            type: 'success',
            message: '‰∏ä‰º†ÊàêÂäü üòä'
          });

          //Êõ¥ÊîπÈªòËÆ§ÂõæÁâáË∑ØÂæÑ
          this.user.avatar = data;
          this.setuser(this.user);
          this.centerDialogVisible = false;//‰øÆÊîπÂ§¥ÂÉèÁöÑÂØπËØùÊ°ÜÊéßÂà∂
        }).catch(err => {
          // eslint-disable-next-line no-console
          console.log('err', err);
        })
        return false;
      })
    },
    //ÁÇπÂáªÂèñÊ∂à‰øÆÊîπÂ§¥ÂÉèÊåâÈíÆ
    noEditAvatar () {
      this.croppa.refresh();
    },
    //ÁßªÈô§Â§¥ÂÉè
    removeAvatarImg () {
      this.$confirm('ÁßªÈô§ËÉåÊôØÂ§¥ÂÉè, ÊòØÂê¶ÁªßÁª≠?', 'ÊèêÁ§∫', {
        confirmButtonText: 'Á°ÆÂÆö',
        cancelButtonText: 'ÂèñÊ∂à',
        type: 'warning'
      }).then(() => {

        //ÂèÇÊï∞
        let paramsObj = {
          type: 'avatar',
        }
        //ÂèëÈÄÅaxios Âà†Èô§ÂõæÁâá
        this.$server.deleteImg(paramsObj).then(data => {
          if (data == 2) {
            this.$message({
              type: 'warning',
              message: 'ËøòÊ≤°ÊúâÂ§¥ÂÉè üòì'
            });
            return false;
          }
          //Êõ¥ÊîπÈªòËÆ§ÂõæÁâáË∑ØÂæÑ
          this.user.avatar = '';
          //Ëß¶ÂèëÊèí‰ª∂ÁßªÈô§
          this.croppa.remove();
          //ÊàêÂäüÊèêÁ§∫
          this.$message({
            type: 'success',
            message: 'ÁßªÈô§ÊàêÂäü!'
          });
          this.user.avatar = '';
          this.setuser(this.user);


        }).catch(err => {
          // eslint-disable-next-line no-console
          console.log(err)
        })

      }).catch(() => {
        this.$message({
          type: 'info',
          message: 'Â∑≤ÂèñÊ∂à'
        });
      });
    }
  }
}
