import { mapState, } from 'vuex'

export default {
  data () {
    return {
      msg: '',
      status: true,
      search: '',
      route: '',
      input6: '',
    }
  },
  computed: {
    ...mapState([
      'verbState',
      'user',
    ]),

  },
  mounted: function () {
    // 根据路径变更颜色
    setTimeout(() => { this.route = this.$route.path }, 100);
  },
  methods: {
    logstatus: function (logstatus) {
      this.status = logstatus;
    },
    //变更颜色根据路径
    change: function (route) {
      this.route = route;
    },
  }
}
