import { mapState, mapMutations } from 'vuex'
export default {
  data () {
    return {
      msg: '',
      status: true,
      search: '',
      route: '',
      input6: '',
      root: '',
    }
  },
  computed: {
    ...mapState([
      'verbState',
      'user',
    ]),

  },
  mounted: function () {
    // 根据路径变更颜色
    setTimeout(() => { this.route = this.$route.path }, 100);
    this.root = this.$URL;
  },
  methods: {
    ...mapMutations({
      setverbState: 'SET_VERBSTATE',
      setToken: 'SET_TOKEN',
    }),
    logstatus: function (logstatus) {
      this.status = logstatus;
    },
    //变更颜色根据路径
    change: function (route) {
      this.route = route;
    },
    handleCommand: function (command) {
      if (command === 'logout') this.logout();
    },
    logout: function () {
      localStorage.removeItem('access_token');
      localStorage.removeItem('verbState');
      localStorage.removeItem('relogin');
      //把登录后的token 存储在vuex 中
      this.setverbState(false);
      this.setToken('');

      this.$message({
        message: '已经成功退出',
        type: 'warning'
      });

      this.$router.push({ name: 'login' });

    },
  }
}
